import {Fragment, useState} from "react";
import classes from './new_patient_style/new_patient.module.css'
import axios from "axios";


function NewPatientPage() {
    const [enteredFirstName, setEnteredFirstName] = useState('Reece');
    const [enteredMiddleName, setEnteredMiddleName] = useState('W');
    const [enteredLastName, setEnteredLastName] = useState('Colbus');
    const [enteredDateOfBirth, setEnteredDateOfBirth] = useState('02/02/0202');
    const [enteredPhoneNumber, setEnteredPhoneNumber] = useState('9099089703');
    const [enteredEmailAddress, setEnteredEmailAddress] = useState('rcolbus@me.com');
    const [enteredAddressLine1, setEnteredAddressLine1] = useState('857 e 25th st');
    const [enteredAddressLine2, setEnteredAddressLine2] = useState('');
    const [enteredCity, setEnteredCity] = useState('upland');
    const [enteredZipCode, setEnteredZipCode] = useState('91784');
    const [enteredState, setEnteredState] = useState('ca');
    const [enteredPhotoPath, setEnteredPhotopath] = useState('');
    const [enteredApptTime, setEnteredApptTime] = useState('');
    const [enteredApptDate, setEnteredApptDate] = useState('');

    function formValidation()
    {
        return !enteredFirstName ||
            enteredFirstName.trim() === '' ||
            !enteredLastName ||
            enteredLastName.trim() === '' ||
            !enteredPhoneNumber ||
            enteredPhoneNumber.trim() === '' ||
            !enteredEmailAddress ||
            !enteredEmailAddress.includes('@') ||
            !enteredAddressLine1 ||
            enteredAddressLine1.trim() === '' ||
            !enteredCity ||
            enteredCity.trim() === '' ||
            !enteredState ||
            enteredState.trim() === '' ||
            !enteredZipCode ||
            enteredZipCode.trim() === '' ||
            !enteredPhotoPath ||
            enteredPhotoPath.trim() === '' ||
            !enteredDateOfBirth ||
            enteredDateOfBirth.trim() === '' ||
            !enteredApptDate ||
            enteredApptDate.trim() === '' ||
            !enteredApptTime ||
            enteredApptTime.trim() === '';
    }

    const postNewPatient = (event) => {
        event.preventDefault();
        if (formValidation()){
            fetch(`http://localhost:8000/api/v1/registrations`, {
                method: 'POST',
                body: JSON.stringify({
                    first_name: enteredFirstName,
                    middle_name: enteredMiddleName,
                    last_name: enteredLastName,
                    phone_number: enteredPhoneNumber,
                    email_address: enteredEmailAddress,
                    street_address_line1: enteredAddressLine1,
                    street_address_line2: enteredAddressLine2,
                    city: enteredCity,
                    state: enteredCity,
                    zip_code: enteredZipCode,
                    photo_path: enteredPhotoPath,
                    birth_date: enteredDateOfBirth,
                    appointment_date: enteredApptDate,
                    appointment_time: enteredApptTime
                }),
            }).then(response => {
                console.log('response', response)
                if(response.status === 200){
                    alert(`Thank you ${enteredFirstName} your appointment on ${enteredApptDate} at ${enteredApptTime} has been saved. Confirmation sent to ${enteredEmailAddress}`)
                }
            }).catch(e=>{
                console.log('e', e)
            })

            }else{
            alert(`${enteredEmailAddress} invalid`)
        }



    }
    return (
        <Fragment>
            <h1 className={classes.title}>Register</h1>
            <form onSubmit={postNewPatient}>
                <div className={classes.centerouter}>
                    <div className={classes.div}>
                        First Name: <input type={"text"} required className={classes.inputfield} value={enteredFirstName} onChange={(event) => setEnteredFirstName(event.target.value)} id='FirstName'/>
                        Middle Initial: <input type={"text"} className={classes.inputfield} value={enteredMiddleName} onChange={(event) => setEnteredMiddleName(event.target.value)} id='MiddleName'/>
                        Last Name: <input type={'text'} required className={classes.inputfield} value={enteredLastName} onChange={(event) => setEnteredLastName(event.target.value)} id='LastName'/>
                    </div>
                    <div className={classes.div}>
                        <label form="DateOfBirth">Date of Birth:</label>
                        <input type={"date"} required className={classes.inputfield} value={enteredDateOfBirth} onChange={(event) => setEnteredDateOfBirth(event.target.value)} id='DateOfBirth'/>
                        <label form="PhoneNumber">Phone Number:</label>
                        <input type={'number'} required className={classes.inputfield} value={enteredPhoneNumber} onChange={(event) => setEnteredPhoneNumber(event.target.value)} id='PhoneNumber'/>
                        <label form="EmailAddress">Email Address:</label>
                         <input type={"email"} required className={classes.inputfield} value={enteredEmailAddress} onChange={(event) => setEnteredEmailAddress(event.target.value)} id='EmailAddress'/>
                    </div>
                    <div className={classes.div}>
                        <label form="AddressLine1">Street Address 1:</label>
                         <input type={'text'} required className={classes.inputfield} value={enteredAddressLine1} onChange={(event) => setEnteredAddressLine1(event.target.value)} id='AddressLine1'/>
                        <label form="AddressLine2">Street Address 2:</label>
                         <input type={'text'}  className={classes.inputfield} value={enteredAddressLine2} onChange={(event) => setEnteredAddressLine2(event.target.value)} id='AddressLine2'/>
                    </div>
                    <div className={classes.div}>
                        City: <input type={'text'} required className={classes.inputfield} value={enteredCity} onChange={(event) => setEnteredCity(event.target.value)} id='City'/>
                        State: <input type={'text'} required className={classes.inputfield} value={enteredState} onChange={(event) => setEnteredState(event.target.value)} id='State'/>
                        Zip code: <input type={'number'} required className={classes.inputfield} value={enteredZipCode} onChange={(event) => setEnteredZipCode(event.target.value)} id='ZipCode'/>
                    </div>
                    <div className={classes.div}>
                        DL Photo: <input type={"file"} required className={classes.inputfield} value={enteredPhotoPath} onChange={(event) => setEnteredPhotopath(event.target.value)} id='PhotoPath'/>
                        Desired Appointment Date: <input type={'date'} required className={classes.inputfield} value={enteredApptDate} onChange={(event) => setEnteredApptDate(event.target.value)} id='ApptDate'/>
                        Desired Appointment Time: <input type={'time'} required className={classes.inputfield} value={enteredApptTime} onChange={(event) => setEnteredApptTime(event.target.value)} id='ApptTime'/>
                    </div>
                    <div>
                        <button type={"submit"}>Submit</button>
                    </div>

                </div>

            </form>


        </Fragment>
    )
}


export default NewPatientPage;
